PREFIX intend: <https://intendproject.eu/schema/>

SELECT ?bandwidth ?latency ?distance ?packetErrorRate
WHERE {
  # Extract ID between the last '(' and the next ')' if present; otherwise use the whole value
  BIND(
    IF(CONTAINS("${datacenter1}", "("),
       REPLACE("${datacenter1}", ".*\\(([^)]*)\\).*", "$1"),
       "${datacenter1}"
    ) AS ?idA
  )
  BIND(
    IF(CONTAINS("${datacenter2}", "("),
       REPLACE("${datacenter2}", ".*\\(([^)]*)\\).*", "$1"),
       "${datacenter2}"
    ) AS ?idB
  )

  # Normalize order so EC_1-EC_2 and EC_2-EC_1 resolve to the same pair IRI
  BIND(IF(STR(?idA) < STR(?idB), ?idA, ?idB) AS ?id1)
  BIND(IF(STR(?idA) < STR(?idB), ?idB, ?idA) AS ?id2)
  BIND(CONCAT(STR(?id1), "-", STR(?id2)) AS ?pairLocal)
  BIND(IRI(CONCAT("https://intendproject.eu/telenor/", ?pairLocal)) AS ?pair)

  GRAPH <http://intendproject.eu/telenor/infra> {
    OPTIONAL { ?pair intend:bandwidth        ?bandwidth }
    OPTIONAL { ?pair intend:latencies        ?latency }
    OPTIONAL { ?pair intend:distance         ?distance }
    OPTIONAL { ?pair intend:packetErrorRate  ?packetErrorRate }
  }
}

PREFIX intend: <https://intendproject.eu/schema/>
PREFIX schema: <https://schema.org/>
PREFIX aeros: <https://aeros.eu/schema/>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>

SELECT ?datacenter ?name ?city ?cpu ?gpu ?memory ?electricity (xsd:double(?cost) AS ?costNum)
WHERE {
  # --- Extract EC_* from variables if they include "(EC_*)" labels; else use whole value ---
  BIND(
    IF(CONTAINS("${datacenter1}", "("),
       REPLACE("${datacenter1}", ".*\\(([^)]*)\\).*", "$1"),
       "${datacenter1}"
    ) AS ?idA
  )
  BIND(
    IF(CONTAINS("${datacenter2}", "("),
       REPLACE("${datacenter2}", ".*\\(([^)]*)\\).*", "$1"),
       "${datacenter2}"
    ) AS ?idB
  )

  GRAPH <http://intendproject.eu/telenor/infra> {
    ?ec a intend:edgeCluster ;
        intend:clusterId ?datacenter ;
        schema:name ?name ;
        intend:latitude ?lat ;
        intend:longitude ?lon .
    OPTIONAL { ?ec aeros:location ?city }
    OPTIONAL { ?ec intend:gpuArchitecture ?gpu }
    OPTIONAL { ?ec aeros:cpuArchitecture ?cpu }
    OPTIONAL { ?ec intend:memory ?memory }
    OPTIONAL { ?ec aeros:majorSourceOfElectricity ?electricity }
    OPTIONAL { ?ec aeros:costOfCompute ?cost }

    FILTER( ?datacenter IN (?idA, ?idB) )
  }
}
ORDER BY ?datacenter

PREFIX schema: <https://schema.org/>
PREFIX intend: <https://intendproject.eu/schema/>
PREFIX aeros: <https://aeros.eu/schema/>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>

SELECT ?id ?name ?city ?cpu ?gpu ?memory ?electricity (xsd:double(?cost) AS ?costNum) (xsd:double(?lat) AS ?latNum) (xsd:double(?lon) AS ?lonNum)
WHERE {
  GRAPH <http://intendproject.eu/telenor/infra> {
    ?ec a intend:edgeCluster ;
        intend:clusterId ?id ;
        schema:name ?name ;
        intend:latitude ?lat ;
        intend:longitude ?lon .
    OPTIONAL { ?ec aeros:location ?city }
    OPTIONAL { ?ec intend:gpuArchitecture ?gpu }
    OPTIONAL { ?ec aeros:cpuArchitecture ?cpu }
    OPTIONAL { ?ec intend:memory ?memory }
    OPTIONAL { ?ec aeros:majorSourceOfElectricity ?electricity }
    OPTIONAL { ?ec aeros:costOfCompute ?cost }
  }
}
ORDER BY ?id

list.definition:
PREFIX schema: <https://schema.org/>
PREFIX intend: <https://intendproject.eu/schema/>

SELECT DISTINCT ?value ?text
WHERE {
  GRAPH <http://intendproject.eu/telenor/infra> {
    ?ec a intend:edgeCluster ;
        intend:clusterId ?id ;
        schema:name ?name .
    BIND(CONCAT(STR(?name), " (", STR(?id), ")") AS ?label)
    BIND(?label AS ?value)   # value used by Grafana
    BIND(STR(?name) AS ?text)  # text shown in dropdown
  }
}
ORDER BY LCASE(?text)

