targets.queryText:
PREFIX met:  <http://tio.models.tmforum.org/tio/v3.6.0/MetricsAndObservations/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX data5g: <http://5g4data.eu/5g4data#>
PREFIX quan: <http://tio.models.tmforum.org/tio/v3.6.0/QuantityOntology/>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX icm: <http://tio.models.tmforum.org/tio/v3.6.0/IntentCommonModel/>
PREFIX imo: <http://tio.models.tmforum.org/tio/v3.6.0/IntentManagementOntology/>
PREFIX log: <http://tio.models.tmforum.org/tio/v3.6.0/LogicalOperators/>
PREFIX set: <http://tio.models.tmforum.org/tio/v3.6.0/SetOperators/>

SELECT ?unit ?value ?timestamp ?storage_type ?prometheus_query ?readable_query
WHERE {
  SERVICE <repository:intents> {
    BIND(data5g:${intent_id} AS ?intent) .

    ?intent log:allOf ?netExpectation .
    ?netExpectation log:allOf ?condition .

    ?condition a icm:Condition ;
               set:forAll [
                 icm:valuesOfTargetProperty ?metricIRI
               ] .

    # Extract condition ID from the metric IRI
    BIND(STRAFTER(STR(?metricIRI), "5g4data#") AS ?condition_id) .
  }

  # Check if Prometheus metadata exists for this condition
  SERVICE <repository:intent-reports> {
    OPTIONAL {
      GRAPH <http://intent-reports-metadata> {
        data5g:${metric_name} data5g:hasPrometheusQuery ?prometheus_query ;
                               data5g:hasReadableQuery ?readable_query .
      }
    }

    # Determine storage type based on metadata
    BIND(IF(BOUND(?prometheus_query), "prometheus", "graphdb") AS ?storage_type) .

    # If GraphDB storage, get the observation data
    OPTIONAL {
      FILTER(?storage_type = "graphdb") .
      BIND(data5g:${metric_name} AS ?metric) .

      ?observation a met:Observation ;
                   met:observedMetric ?metric ;
                   met:observedValue ?blankValue ;
                   met:obtainedAt ?timestamp .

      ?blankValue rdf:value ?rawValue ;
                  quan:unit ?unit .

      BIND(xsd:decimal(?rawValue) AS ?value) .
    }

    # If Prometheus storage, create placeholder data for Grafana to handle
    OPTIONAL {
      FILTER(?storage_type = "prometheus") .
      # Create a dummy observation to indicate Prometheus storage
      BIND("prometheus" AS ?unit) .
      BIND(0 AS ?value) .
      BIND(NOW() AS ?timestamp) .
    }
  }
}
ORDER BY ?timestamp

PREFIX log: <http://tio.models.tmforum.org/tio/v3.6.0/LogicalOperators/>
PREFIX icm: <http://tio.models.tmforum.org/tio/v3.6.0/IntentCommonModel/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX dct: <http://purl.org/dc/terms/>
PREFIX data5g: <http://5g4data.eu/5g4data#>
PREFIX set: <http://tio.models.tmforum.org/tio/v3.6.0/SetOperators/>
PREFIX quan: <http://tio.models.tmforum.org/tio/v3.6.0/QuantityOntology/>

SELECT DISTINCT 
  (STRAFTER(STR(?intent), "5g4data#") AS ?intent_id)
  (GROUP_CONCAT(DISTINCT ?description; separator="; ") AS ?intent_description)
  (GROUP_CONCAT(DISTINCT ?condition_description; separator="; ") AS ?condition_descriptions)
WHERE {
  SERVICE <repository:intents> {
    BIND(data5g:${intent_id} AS ?intent)
    ?intent rdf:type ?intentType ;
            log:allOf ?expectation .

    ?expectation rdf:type ?expectationType ;
                 dct:description ?description .

    OPTIONAL {
      ?expectation log:allOf ?condition .
      ?condition rdf:type icm:Condition .

      OPTIONAL {
        ?condition set:forAll [
          icm:valuesOfTargetProperty ?metric
        ] .
      }

      OPTIONAL {
        ?condition dct:description ?condition_description .
      }
    }

    FILTER(CONTAINS(STR(?expectationType), "Expectation"))
  }
}
GROUP BY ?intent
LIMIT 10
