PREFIX icm: <http://tio.models.tmforum.org/tio/v3.6.0/IntentCommonModel/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX log: <http://tio.models.tmforum.org/tio/v3.6.0/LogicalOperators/>
PREFIX data5g: <http://5g4data.eu/5g4data#>

SELECT (COUNT(DISTINCT ?intent) AS ?totalIntents)
WHERE {
  SERVICE <repository:intents_and_intent_reports> {
    ?intent rdf:type icm:Intent ;
            ?op1 ?expectation .
    FILTER(STRSTARTS(STR(?op1), STR(log:)))

    ?expectation ?op2 ?context .
    FILTER(STRSTARTS(STR(?op2), STR(log:)))

    ?context a icm:Context ;
             data5g:DataCenter ?dc .
    FILTER(STR(?dc) = "${datacenter}")
  }
}

targets.queryText:
PREFIX icm: <http://tio.models.tmforum.org/tio/v3.6.0/IntentCommonModel/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX log: <http://tio.models.tmforum.org/tio/v3.6.0/LogicalOperators/>
PREFIX data5g: <http://5g4data.eu/5g4data#>

SELECT (COUNT(DISTINCT ?intent) AS ?totalIntents)
WHERE {
  SERVICE <repository:intents_and_intent_reports> {
    # Intent must reference a DeploymentExpectation via a logical operator
    ?intent rdf:type icm:Intent ;
            ?op1 ?de .
    FILTER(STRSTARTS(STR(?op1), STR(log:)))

    # That Expectation must be a DeploymentExpectation, and reference a Context via a log:* operator
    ?de a data5g:DeploymentExpectation ;
        ?op2 ?context .
    FILTER(STRSTARTS(STR(?op2), STR(log:)))

    # The Context must specify the target data center
    ?context a icm:Context ;
             data5g:DataCenter ?dc .

    # Normalize incoming var to match stored value (EC21 vs EC_21)
    BIND(REPLACE("${datacenter}", "_", "") AS ?dcParam)

    FILTER(STR(?dc) = ?dcParam)
  }
}

PREFIX intend: <https://intendproject.eu/schema/>
PREFIX schema: <https://schema.org/>
PREFIX aeros: <https://aeros.eu/schema/>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>

SELECT ?datacenter ?name ?city ?cpu ?gpu ?memory ?electricity
WHERE {
  BIND(
    IF(CONTAINS("${datacenter}", "("),
       REPLACE("${datacenter}", ".*\\(([^)]*)\\).*", "$1"),
       "${datacenter}"
    ) AS ?idA
  )

  GRAPH <http://intendproject.eu/telenor/infra> {
    ?ec a intend:edgeCluster ;
        intend:clusterId ?datacenter ;
        schema:name ?name ;
        intend:latitude ?lat ;
        intend:longitude ?lon .
    OPTIONAL { ?ec aeros:location ?city }
    OPTIONAL { ?ec intend:gpuArchitecture ?gpu }
    OPTIONAL { ?ec aeros:cpuArchitecture ?cpu }
    OPTIONAL { ?ec intend:memory ?memory }
    OPTIONAL { ?ec aeros:majorSourceOfElectricity ?electricity }

    FILTER( ?datacenter IN (?idA) )
  }
}
ORDER BY ?datacenter

PREFIX log: <http://tio.models.tmforum.org/tio/v3.6.0/LogicalOperators/>
PREFIX icm: <http://tio.models.tmforum.org/tio/v3.6.0/IntentCommonModel/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX dct: <http://purl.org/dc/terms/>
PREFIX data5g: <http://5g4data.eu/5g4data#>
PREFIX set: <http://tio.models.tmforum.org/tio/v3.6.0/SetOperators/>
PREFIX quan: <http://tio.models.tmforum.org/tio/v3.6.0/QuantityOntology/>

SELECT DISTINCT 
  (STRAFTER(STR(?intent), "5g4data#") AS ?intent_id)
  (GROUP_CONCAT(DISTINCT ?description; separator="; ") AS ?intent_description)
  (CONCAT(
    "http://start5g-1.cs.uit.no:3002/d/fekk4b61d38qof/intent-and-condition-metrics-timeseries-dashboard?var-intent_id=",
    STRAFTER(STR(?intent), "5g4data#"),
    "&var-condition_metrics=",
    GROUP_CONCAT(DISTINCT 
      CONCAT(
        STRAFTER(STR(?metric), "5g4data#"),
        "_",
        STRAFTER(STR(?condition), "5g4data#")
      ); 
      separator=","
    )
  ) AS ?intent_link)
  (GROUP_CONCAT(DISTINCT ?condition_description; separator="; ") AS ?condition_descriptions)
WHERE {
  SERVICE <repository:intents_and_intent_reports> {
    ?intent rdf:type ?intentType ;
            log:allOf ?expectation .

    ?expectation rdf:type ?expectationType ;
                 dct:description ?description .

    OPTIONAL {
      ?expectation log:allOf ?condition .
      ?condition rdf:type icm:Condition .

      OPTIONAL {
        ?condition set:forAll [
          icm:valuesOfTargetProperty ?metric
        ] .
      }

      OPTIONAL {
        ?condition dct:description ?condition_description .
      }
    }

    FILTER(CONTAINS(STR(?expectationType), "Expectation"))
  }
}
GROUP BY ?intent

PREFIX icm: <http://tio.models.tmforum.org/tio/v3.6.0/IntentCommonModel/>
PREFIX imo: <http://tio.models.tmforum.org/tio/v3.6.0/IntentModelOntology/>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX log: <http://tio.models.tmforum.org/tio/v3.6.0/LogicalOperators/>
PREFIX data5g: <http://5g4data.eu/5g4data#>

SELECT (STRAFTER(STR(?intent), "5g4data#") AS ?intent_id)
       (STRAFTER(STR(?state), "IntentModelOntology/") AS ?intent_state)
WHERE {
  SERVICE <repository:intents_and_intent_reports> {
    {
      SELECT ?intent (MAX(?generated) AS ?latestGenerated)
      WHERE {
        ?report a icm:IntentReport ;
                icm:about ?intent ;
                icm:reportGenerated ?generated .
      }
      GROUP BY ?intent
    }

    # Keep only intents that belong to the selected datacenter (same normalization)
    FILTER EXISTS {
      ?intent ?op1 ?expOrCtx .
      FILTER(STRSTARTS(STR(?op1), STR(log:)))
      ?expOrCtx ?op2 ?context .
      FILTER(STRSTARTS(STR(?op2), STR(log:)))
      ?context a icm:Context ;
               data5g:DataCenter ?dc .
      BIND(REPLACE("${datacenter}", "_", "") AS ?dcParam)
      FILTER(STR(?dc) = ?dcParam)
    }

    ?report a icm:IntentReport ;
            icm:about ?intent ;
            icm:reportGenerated ?latestGenerated ;
            icm:intentHandlingState ?state .
  }
}
